import React,{useEffect,useRef} from 'react'
import {Card, Text, View} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Image, Dimensions ,StyleSheet,ScrollView,PixelRatio, Switch,ImageBackground,Platform,KeyboardAvoidingView,Keyboard, FlatList,Modal, ListViewComponent, Alert, TouchableHighlight, TouchableOpacity, TextInput, ActivityIndicator} from 'react-native';
import { FontAwesome5, FontAwesome ,Octicons,Ionicons} from '@expo/vector-icons';
import { AntDesign, EvilIcons, MaterialCommunityIcons } from '@expo/vector-icons';
import firebase from '../firebaseConfig';
import * as ImagePicker from 'expo-image-picker';




export default function EditProfile(props,navigation) {

  const [userData, setUserData] = React.useState({});
  const [image, setImage] = React.useState("");
  const [imageURL , setImageURL] = React.useState("");
  const [name, setName] = React.useState("");
  const [uploading, setUploading] = React.useState(false);
  const [file, setFile] = React.useState('');
  const [fileData, setFileData] = React.useState('');
  const [newFileName, setNewFileName] = React.useState('');
  const [fileUploading, setFileUploading] = React.useState(false);
  const [imageChanged, setImageChanged] = React.useState(false);


  useEffect(() => {

    firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).collection("people").doc(props?.route?.params?.profileUID)
    .onSnapshot((querySnapshot) => {
        setUserData(querySnapshot?.data())
        setImage(querySnapshot?.data()?.image)
        setName(querySnapshot?.data()?.name)
    })


}, [])


const createURLAndUploadData = (a,b)=>{

    console.log(a,b)

    setFileUploading(true)
    setFile(a)
    setFileData(b)

    new Promise(async (res, rej) => {

        const response = await fetch(b);
        console.log("response ===> ",response)


        const file = await response.blob();
        let upload = firebase.storage().ref(`/filesPeopleImage/${props?.route?.params?.uid}/${Date.now()}${Math.random().toString(36).slice(2)}`).put(file);

        console.log("Upload ===> ",upload)
        upload.on( 'state_changed', snapshot => {},
                                              err => {
                                                        rej(err);
                                                        alert(err)
                                                       },
                                              async () => {
                                                        setFileUploading(false)
                                                        const url = await upload.snapshot.ref.getDownloadURL()
                                                        .then((url) => {
                                                            console.log("URL ---> " + url)
                                                            setImageURL(url)
                                                            setFileUploading(false)
                                                            firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).collection("people").doc(props?.route?.params?.profileUID).update({ "lastEdited" : Date.now(), name :name, image: url })
                                                            .then((res)=> props?.navigation?.navigate("HomeScreen") )
                                                        })
                                                        .catch(err=>{
                                                            alert(err);
                                                            setFile(null)
                                                            setFileUploading(false)
                                                        })
                                                    }
                  );
      });






}


const pickImage = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 0.5,
    });

    console.log("result?.uri ==> " , result);

    if (!result?.canceled) {
      setImage(result?.uri);
      setNewFileName(result?.fileName ? result?.fileName : Date.now() )
      setImageChanged(true)
    }

  };


  const onSubmit =  () => {

    if(name == "" || image == ""){
        console.log("NAME ===> ",name)
        console.log("Image ===> ",image)

      return  alert("Name & Profile Picture is required")
    }

    else{
        setUploading(true)
        if(!imageChanged) firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).collection("people").doc(props?.route?.params?.profileUID).update({ "lastEdited" : Date.now(), name :name })
        .then((res)=> props?.navigation?.navigate("HomeScreen") )
        else createURLAndUploadData(newFileName,image)
    }



  };


  const deleteProfile=()=>{


    Alert.alert('Delete Profile', 'Once deleted will be not be recovered', [
      {
        text: 'Delete',
        onPress: () => {
          firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).collection("people").doc(props?.route?.params?.profileUID).delete().then(()=>{
            props?.navigation?.navigate("HomeScreen")
          })
        },
      },{
        text: "Cancel",
        onPress: () => console.log("Cancel Pressed"),
        style: "cancel"
      },
    ]);




  }



  return ( 

<KeyboardAvoidingView
      behavior={Platform.OS === "ios" ? "padding" : "height"}
      keyboardVerticalOffset={0}
      style={{flex:1,backgroundColor:"#fff"}}
    >
    <View style={{flexDirection:"row",justifyContent:"space-between",alignItems:"center",marginTop:50,marginBottom:20}}>

                <TouchableOpacity style={{flexDirection:"row",justifyContent:"space-evenly",alignItems:"center",marginLeft:10}}  onPress={()=> props?.navigation?.navigate("HomeScreen")}  >
                        <Ionicons name="md-chevron-back-outline" size={40} color="black" />
                </TouchableOpacity>

                <TouchableOpacity style={{backgroundColor:"#bd0000",margin:10,borderRadius:6}}  onPress={()=> deleteProfile()}  >
                    <Text style={{fontSize:18,fontFamily:"UberMoveMedium",color:"#FFF",padding:10}}>Delete Profile</Text>
                </TouchableOpacity>
    </View>

    <TouchableOpacity onPress={()=>pickImage()} style={{margin:15, width:120,height:120,borderRadius:360,backgroundColor:"#EEE",flexDirection:"row",justifyContent:"center",alignItems:"center"}}   >


        { image 

                ?<Image
                                                    style={styles.tinyLogo}
                                                    source={{uri: image}}
                                                />
                :<View style={{display:"flex",justifyContent:"center",alignSelf:"center"}}>
                <Octicons name="plus-circle" size={24} color="black" />
                </View>

        }                   



    </TouchableOpacity>
     <Text style={{marginTop:-5,marginLeft:35,color:"#000",fontFamily:"UberMoveRegular",fontSize:Platform.OS === "android" ? 10.5:12}}   >Edit Profile Pic</Text>



    <TextInput
                                      value={name}
                                      onChangeText={e => setName( e)}
                                      placeholder="Full Name"
                                      clearable
                                      editable
                                    backgroundColor={"#EEE"}
                                    padding={20}
                                    margin={20}
                                    marginTop={50}
                                    fontSize={17}
                                    returnKeyType={"done"}
                                    autoCapitalize={true}
                                    />

<TextInput
                                      value={userData?.phonenumber}
                                      placeholder="Full Name"
                                      clearable
                                      editable = {false}
                                    backgroundColor={"#EEE"}
                                    padding={20}
                                    margin={20}
                                    marginTop={5}
                                    fontSize={17}
                                    returnKeyType={"done"}
                                    autoCapitalize={true}
                                    />

{ imageChanged || name != userData?.name  ?

                                <TouchableOpacity   onPress={()=>onSubmit()}   style={{marginHorizontal:"5%",width:"90%" ,flexDirection:"row",justifyContent:"center",alignItems:"center",height:60,marginVertical:10,backgroundColor:"#000"}}>



                                          {uploading
                                            ?<View style={{flexDirection:"row",fontFamily:"UberMoveLight",justifyContent:"center",alignContent:"center"}}><ActivityIndicator color={

} color="#FFF" size={30} ></ActivityIndicator></View>
                                            :  <View style={{flexDirection:"row",justifyContent:"center",alignItems:"center",padding:2.5}}><Octicons name="check" size={34} color="white" /></View>
                                        } 


                                 </TouchableOpacity>

                                 : null}





</KeyboardAvoidingView>



  );
}


const styles = StyleSheet.create({
    container: {
      flex: 1, 
    },
    addButton:{


        backgroundColor: "#EEE",
        padding:5,
        paddingHorizontal:10,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 0,
        color:"#000",
        width:"100%"

    },
    actStyle:{


        backgroundColor: "#000",
        padding:5,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 10,
        color:"#FFF",
        width:"100%"

    },
    inactStyle:{


        backgroundColor: "#EEE",
        padding:5,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 10,
        color:"#000",
        width:"100%"

    },
    image: {
      resizeMode: 'center',

  },
  tinyLogo: {
    width: 110,
    height: 110,
    borderRadius:360,

  },

})
