import React,{useEffect,useRef} from 'react'
import {Card, Text, View} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Image, Dimensions ,StyleSheet,ScrollView,PixelRatio, Switch,ImageBackground,Platform,KeyboardAvoidingView,Keyboard, FlatList,Modal, ListViewComponent, Alert, TouchableHighlight, TouchableOpacity, TextInput, ActivityIndicator} from 'react-native';
import { FontAwesome5, Ionicons,FontAwesome ,Octicons} from '@expo/vector-icons';
import { AntDesign, EvilIcons, MaterialCommunityIcons } from '@expo/vector-icons';
import * as ImagePicker from 'expo-image-picker';
import firebase from '../firebaseConfig';




export default function AddProfile(props,navigation) {




  const [userData, setUserData] = React.useState({});
  const [image, setImage] = React.useState("");
  const [imageURL , setImageURL] = React.useState("");
  const [name, setName] = React.useState("");
  const [uploading, setUploading] = React.useState(false);
  const [file, setFile] = React.useState('');
  const [fileData, setFileData] = React.useState('');
  const [newFileName, setNewFileName] = React.useState('');
  const [fileUploading, setFileUploading] = React.useState(false);



  useEffect(() => {



}, [])


const createURLAndUploadData = (a,b)=>{

    console.log(a,b)

    setFileUploading(true)
    setFile(a)
    setFileData(b)

    new Promise(async (res, rej) => {

        const response = await fetch(b);
        console.log("response ===> ",response)


        const file = await response.blob();
        let upload = firebase.storage().ref(`/filesPeopleImage/${props?.route?.params?.uid}/${Date.now()}${Math.random().toString(36).slice(2)}`).put(file);

        console.log("Upload ===> ",upload)
        upload.on( 'state_changed', snapshot => {},
                                              err => {
                                                        rej(err);
                                                        alert(err)
                                                       },
                                              async () => {
                                                        setFileUploading(false)
                                                        const url = await upload.snapshot.ref.getDownloadURL()
                                                        .then((url) => {
                                                            console.log("URL ---> " + url)
                                                            setImageURL(url)
                                                            setFileUploading(false)
                                                            firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).collection("people").add({ "createdTimestamp" : Date.now(), name :name, image: url })
                                                            .then((res)=> props?.navigation?.navigate("HomeScreen") )
                                                        })
                                                        .catch(err=>{
                                                            alert(err);
                                                            setFile(null)
                                                            setFileUploading(false)
                                                        })
                                                    }
                  );
      });






}


const pickImage = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 0.5,
    });

    console.log("result?.uri ==> " , result);

    if (!result?.canceled) {
      setImage(result?.uri);
      setNewFileName(result?.fileName ? result?.fileName : Date.now() )
    }





  };

  const createProfile =  () => {



    if(name == "" || image == ""){
        console.log("NAME ===> ",name)
        console.log("Image ===> ",image)

      return  alert("Name & Profile Picture is required")
    }

    else{
        setUploading(true)
        createURLAndUploadData(newFileName,image)
    }



  };


  return ( 

<KeyboardAvoidingView
      behavior={Platform.OS === "ios" ? "padding" : "height"}
      keyboardVerticalOffset={0}
      style={{flex:1,backgroundColor:"#fff",paddingTop:50}}
    >

            <View style={{flexDirection:"row",justifyContent:"space-between",alignItems:"center",marginBottom:25,marginTop:20}}>

            <TouchableOpacity style={{flexDirection:"row",justifyContent:"space-evenly",alignItems:"center",marginLeft:10}}  onPress={()=> props?.navigation?.navigate("HomeScreen")}  >
                    <Ionicons name="md-chevron-back-outline" size={30} color="black" />
                    <Image style={{width:35,height:35,borderRadius:360}} source={{uri : userData?.image}} />
            </TouchableOpacity>

            <Text style={{fontSize:28,fontFamily:"UberMoveMedium",color:"#000",marginRight:20}}>New Employee</Text>
            </View>

            <TouchableOpacity onPress={()=>pickImage()} style={{marginLeft:"32%",margin:15,marginTop:25, width:120,height:120,borderRadius:360,backgroundColor:"#EEE",flexDirection:"row",justifyContent:"center",alignItems:"center"}}   >


                  { image

                          ?<Image
                                                              style={styles.tinyLogo}
                                                              source={{uri: image,}}
                                                          />
                          :<View style={{display:"flex",justifyContent:"center",alignSelf:"center"}}>
                          <Octicons name="plus-circle" size={24} color="black" />
                          </View>

              }                   


            </TouchableOpacity>




            <TextInput  value={name}
                                              onChangeText={e => setName( e)}
                                              placeholder="Full Name"
                                              clearable
                                              editable
                                            backgroundColor={"#EEE"}
                                            padding={20}
                                            margin={20}
                                            marginTop={50}
                                            fontSize={17}
                                            returnKeyType={"done"}
              autoCapitalize={true}
             />

            {  name && image ?<TouchableOpacity   onPress={()=>createProfile()}   style={{marginHorizontal:"5%",width:"90%" ,flexDirection:"row",justifyContent:"center",alignItems:"center",height:60,marginVertical:10,backgroundColor:"#000"}}>



                                         {uploading
                                           ?<View style={{flexDirection:"row",fontFamily:"UberMoveLight",justifyContent:"center",alignContent:"center"}}><ActivityIndicator color={

} color="#FFF" size={30} ></ActivityIndicator></View>
                                           :  <View style={{flexDirection:"row",justifyContent:"center",alignItems:"center",padding:2.5}}><Octicons name="check" size={34} color="white" /></View>
                                       } 


                                </TouchableOpacity>

                                : null}

</KeyboardAvoidingView>



  );
}


const styles = StyleSheet.create({
    container: {
      flex: 1, 
    },
    addButton:{


        backgroundColor: "#EEE",
        padding:5,
        paddingHorizontal:10,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 0,
        color:"#000",
        width:"100%"

    },
    actStyle:{


        backgroundColor: "#000",
        padding:5,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 10,
        color:"#FFF",
        width:"100%"

    },
    inactStyle:{


        backgroundColor: "#EEE",
        padding:5,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 10,
        color:"#000",
        width:"100%"

    },
    image: {
      resizeMode: 'center',

  },
  tinyLogo: {
    width: 110,
    height: 110,
    borderRadius:360,

  },

})
