import React,{useEffect,useRef} from 'react'
import {Card, Text, View} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Feather } from '@expo/vector-icons';
import firebase from '../firebaseConfig';
import { Image, Dimensions ,StyleSheet,ScrollView,PixelRatio, Switch,ImageBackground,Platform,KeyboardAvoidingView,Keyboard, FlatList,Modal, ListViewComponent, Alert, TouchableHighlight, TouchableOpacity, TextInput, ActivityIndicator} from 'react-native';
import LottieView from 'lottie-react-native';
import MapView, { Marker,AnimatedRegion, Animated } from 'react-native-maps';


export default function HomeScreen(props,navigation) {

  const [peopleData, setPeopleData] = React.useState([]);
  const [imageLoading, setImageLoading] = React.useState(false);
  const animationRef = React.useRef();
  const [userData, setUserData] = React.useState([]);

  const [mapRegion, setmapRegion] = React.useState({});

  useEffect(() => {
    animationRef.current?.play()
  }, [])


  useEffect(() => {

    firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).collection("people").onSnapshot((querySnapshot) => {

      var data = []

      querySnapshot.forEach((documentSnapshot) => {
            data.push({
                uid : documentSnapshot?.id,
                ...documentSnapshot?.data()
                })

        })

      setPeopleData(data)

    })

    firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).onSnapshot(doc=>{
      setUserData(doc?.data())
    })

  }, []);


  return ( 

    <View style={{backgroundColor:"#FFF",flex:1}}>

{/* <MapView
        style={[styles.map,{height:"30%",alignItems:"stretch"}]}
        region={{latitude : userData?.latitude, longitude : userData?.longitude,latitudeDelta: 0.0043,
          longitudeDelta: 0.0034}}
      >
        <Marker coordinate={{latitude : userData?.latitude, longitude : userData?.longitude,latitudeDelta: 0.0043,
        longitudeDelta: 0.0034}} 
        title='Office' pinColor='red' draggable style={{width:200,height:200}} >
        </Marker>
      </MapView> */}

                <View style={{flexDirection:"row",flexWrap:"wrap",justifyContent:"space-between",width:"100%",alignItems:"flex-start",margin:10,marginTop:25}}>

                <Text style={{fontSize:40,fontFamily:"UberMoveMedium",color:"#000",marginLeft:10}}>People</Text>

                <View style={{flexDirection:"row",justifyContent:"space-between",alignItems:"center"}}>

                    <TouchableOpacity style={{marginRight:10}} onPress={()=> props?.navigation?.navigate("AddProfile")}  ><View style={styles.addButton} >
                        <View style={{display:"flex",justifyContent:"center",alignSelf:"center",margin:9 }}><Feather name="plus" size={Platform.OS === "android" ? 20:24} color={ "#000"} /></View>
                        </View>
                    </TouchableOpacity>

                    <TouchableOpacity style={{marginRight:40}} onPress={()=> props?.navigation?.navigate("SettingScreen")}  ><View style={styles.addButton} >
                        <View style={{display:"flex",justifyContent:"center",alignSelf:"center",margin:9 }}><Feather name="settings" size={Platform.OS === "android" ? 20:24} color={ "#000"} /></View>
                        </View>
                    </TouchableOpacity>

                </View>


                    </View>



                    {peopleData.length != 0
                          ?<ScrollView style={{backgroundColor:"#FFF",padding:10,}}>
                                      <View style={{flexDirection:"row",flex:1,paddingBottom:150,flexWrap:"wrap",width:"100%",justifyContent:"flex-start",alignItems:"center"}}>


                                    {  peopleData.map((data,key)=>{

                                              return <TouchableOpacity id={key} style={{margin:9.5}} onLongPress={()=>props?.navigation?.navigate("EditProfile",{profileUID : data?.uid} )  } onPress={()=> props?.navigation?.navigate("ViewProfile",{profileUID : data?.uid})  }  >

                                                              <View style={{display:"flex",justifyContent:"center",alignSelf:"center",borderWidth:data?.active_session_id ?2 :1,
                                                              backgroundColor:"#EEE",borderRadius:6, borderColor :  data?.active_session_id ? "green" : "white"}}>
                                                              {/* {imageLoading && <View style={{justifyContent:"center",width:100,height:100,}}><ActivityIndicator color={

} size="small" color="black" /></View> } */}

                                                                  <Image
                                                                          style={ styles.tinyLogo }
                                                                          source={{ uri: data?.image}}
                                                                          onLoadStart={()=>setImageLoading(true)}
                                                                          onLoadEnd={()=>setImageLoading(false)}
                                                                      />

                                                              </View>


                                                          <Text style={{marginTop:5,textAlign:"center",color:"#000",fontFamily:"UberMoveMedium",fontSize:14,}} numberOfLines={1} >{ ((data?.name).split(" ")[0].length > 8) ? (((data?.name.split(" ")[0]).substring(0,8-2)) + '...') : data?.name.split(" ")[0] }</Text>

                                                      </TouchableOpacity>;
                                          })}


                                      </View>

                                      </ScrollView>
                          :<View style={{flex:1,width:"60%",marginLeft:"18%",marginTop:100}}>
                            <LottieView
                                ref={animationRef}
                                autoPlay
                                loop={true}
                                style={{flex:1,justifyContent:"center",alignItems:"center"}}
                                source={require("../assets/qr.json")}
                              />
                          </View>
                  }





    </View>






  );
}


const styles = StyleSheet.create({
    container: {
      flex: 1, 
    },
    map: {
    },
    actStyle:{


        backgroundColor: "#000",
        padding:5,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 10,
        color:"#FFF",
        width:"100%"

    },
    inactStyle:{


        backgroundColor: "#EEE",
        padding:0,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 10,
        color:"#000",
        width:"100%"

    },
    addButton:{


        backgroundColor: "#EEE",
        padding:5,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 0,
        color:"#000",
        width:"100%"

    },
    image: {
      resizeMode: 'center',

  },
  tinyLogo: {
    width: 70,
    height: 70,
    borderRadius:6
  },

})
