                    </TouchableOpacity>

              <ScrollView style={{backgroundColor:"#FFF"}}>

              <TouchableOpacity  style={{padding:20,width:"35%",marginLeft:"33%",marginTop:"8%",borderRadius:6, backgroundColor:"#000",marginBottom:15,
