                                                    }}>

<Text  style={{fontFamily:"UberMoveBold",fontSize:14.5, marginBottom:15,textAlign:"center",color:"#FFF"}}>Your QR Code</Text>


                        <QRCode quietZone={5} size={Dimensions.get('window').width/4} value={`https://1qr.co.in/attendance${userData?.generating_uid}`}/>



              </TouchableOpacity>



