import React,{useEffect,useRef} from 'react'
import {Card, Text, View, TextField} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Feather } from '@expo/vector-icons';
import firebase from '../firebaseConfig';
import { Image, Dimensions ,StyleSheet,ScrollView,PixelRatio, Switch,ImageBackground,Platform,KeyboardAvoidingView,Keyboard, FlatList,Modal, ListViewComponent, Alert, TouchableHighlight, TouchableOpacity, TextInput} from 'react-native';
import { FontAwesome5, FontAwesome ,Ionicons} from '@expo/vector-icons';
import { AntDesign, EvilIcons, MaterialCommunityIcons } from '@expo/vector-icons';
import { SimpleLineIcons } from '@expo/vector-icons';
import PhoneInput from 'react-native-phone-number-input';
import { Fontisto } from '@expo/vector-icons';
import { Octicons } from '@expo/vector-icons';
import axios from 'axios';

import * as DocumentPicker from 'expo-document-picker';

import RBSheet from "react-native-raw-bottom-sheet";
import { WebView } from 'react-native-webview';
import LottieView from 'lottie-react-native';
import * as Linking from 'expo-linking';
import QRCode from 'react-native-qrcode-svg';
import {captureRef} from "react-native-view-shot";
import * as MediaLibrary from 'expo-media-library';
import * as Sharing from 'expo-sharing';
import * as FileSystem from 'expo-file-system';
import * as Permissions from 'expo-permissions';
import * as StoreReview from 'expo-store-review';

export default function QRPage(props,navigation) {



  const [userData, setUserData] = React.useState({});
  const [QRShown, setQRShown] = React.useState(true);
  const [showBuyNow, setShowBuyNow] = React.useState(true);
  let down = useRef(null);




  useEffect(() => {

      console.log(props?.route?.params?.uid)
      firebase.firestore().collection('users').doc(props?.route?.params?.uid).onSnapshot(doc => {setUserData(doc?.data())})

  }, []);




  const downloadQR=async()=>{




    if(Platform.OS == "android"){

      const perm = await Permissions.askAsync(Permissions.MEDIA_LIBRARY);
if (perm.status != 'granted') {
  return;
}

try {

        const result = await captureRef(down, {
        result: "tmpfile",
        quality: 1,
        format: "png",
      });
      console.log("result",JSON.stringify(result))
  const asset = await MediaLibrary.createAssetAsync(result);
  console.log("asset",JSON.stringify(asset))
  const album = await MediaLibrary.getAlbumAsync('1QR');
  console.log("album", JSON.stringify(album))
  if (album == null) {
    await MediaLibrary.createAlbumAsync('1QR', asset, false);

  } else {
    await MediaLibrary.addAssetsToAlbumAsync([asset], album, false);
  }
  alert("Saved to your phone gallery")
} catch (e) {
  alert(e);
}

    }

    else{

      try {

        const result = await captureRef(down, {
          result: "tmpfile",
          quality: 1,
          format: "png",
        });

        MediaLibrary.saveToLibraryAsync(result);


        const localuri = await FileSystem.downloadAsync(result, FileSystem.documentDirectory + "filename")
  const asset = await MediaLibrary.createAssetAsync(localuri)
  const album = await MediaLibrary.createAlbumAsync("DownLoads", asset);



      } catch (e) {
        alert("Saved to your phone gallery")

      }

    }

    }



  return (

    <SafeAreaView style={{backgroundColor:"#FFF"}}>

                <View style={{flexDirection:"row",flexWrap:"wrap",justifyContent:"space-between",width:"100%",alignItems:"flex-start",margin:10,}}>

                    <Text style={{fontSize:40,fontFamily:"UberMoveMedium",color:"#000",marginLeft:10}}>QR Code</Text>

                    <TouchableOpacity  onPress={()=> downloadQR()} style={{marginRight:30,marginTop:3, borderColor:"#EEE",padding:10,paddingHorizontal:20,backgroundColor:"#EEE",borderRadius:20}} >
                        <Text style={{fontFamily:"UberMoveBold",fontSize:17,color:"#000",}}>Save</Text>

                </TouchableOpacity>



                </View>





                {userData?.name 
                ?<View style={{borderColor:"#000",paddingTop:100,paddingBottom:50,paddingHorizontal:0,flexDirection:"column",justifyContent:"center",alignContent:"center",alignSelf:"center",alignItems:"center",backgroundColor:"#FFF",marginBottom:300 }}>



                <QRCode
                logoSize={75}
                logo={require("../assets/1qrlogomargin.png")} 
                logoBorderRadius={9} 
                logoMargin={8} 
                quietZone={5} 
                size={(Dimensions.get('window').width)-20} 
                width={"100%"} height={"100%"} 
                value={`https://1qr.co.in/attendance${userData?.generating_uid}`}
                />

                <Text style={{fontSize:18,paddingTop:10,fontFamily:"UberMoveRegular",textAlign:"center",paddingBottom:15,backgroundColor:"#fff",color:"#000"}}>powered by 1QR.co.in</Text>

                </View>
                :null

                }


<View ref={down} style={{marginTop:300,paddingTop:50,paddingBottom:50,paddingHorizontal:20,flexDirection:"column",justifyContent:"center",alignContent:"center",alignSelf:"center",alignItems:"center",backgroundColor:"#FFF",marginBottom:100 }}>

<View  style={{padding:5,borderRadius:100,borderColor:"#000",borderWidth:5,backgroundColor:"#000",paddingHorizontal:20,paddingVertical:5,marginBottom:50}}><Text style={{fontSize:20,fontFamily:"UberMoveBold",textAlign:"center",color:"#fff"}}>{userData?.name}</Text></View>


                <QRCode
                logoSize={75}
                logo={require("../assets/1qrlogomargin.png")} 
                logoBorderRadius={9} 
                logoMargin={8}
                quietZone={5} size={(Dimensions.get('window').width)-40} width={"100%"} height={"100%"} value={`https://1qr.co.in/attendance${userData?.generating_uid}`}/>
                <Text style={{fontSize:18,paddingTop:10,fontFamily:"UberMoveRegular",textAlign:"center",paddingBottom:15,backgroundColor:"#fff",color:"#000"}}>powered by 1QR.co.in</Text>

                </View>






      </SafeAreaView>




  );
}


const styles = StyleSheet.create({
    container: {
      flex: 1, 
    },
    actStyle:{


        backgroundColor: "#000",
        padding:5,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 10,
        color:"#FFF",
        width:"100%"

    },
    inactStyle:{


        backgroundColor: "#EEE",
        padding:5,
        borderColor:"#000",
        borderRadius: 6,
        marginTop: 10,
        color:"#000",
        width:"100%"

    },
    image: {
      resizeMode: 'center',

  },

})
