import React,{useEffect,useRef} from 'react'
import {Card, Text, View} from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Image, Dimensions ,StyleSheet,ScrollView,PixelRatio, Switch,ImageBackground,Platform,KeyboardAvoidingView,Keyboard, FlatList,Modal, ListViewComponent, Alert, TouchableHighlight, TouchableOpacity, TextInput, ActivityIndicator} from 'react-native';
import { FontAwesome5, FontAwesome ,Octicons,Ionicons} from '@expo/vector-icons';
import { AntDesign,Feather, EvilIcons, MaterialCommunityIcons,MaterialIcons } from '@expo/vector-icons';
import firebase from '../firebaseConfig';
import CalendarHeatmap from 'react-native-calendar-heatmap';
import { Calendar,CalendarList } from 'react-native-calendars';
import LottieView from 'lottie-react-native';
import RBSheet from "react-native-raw-bottom-sheet";



export default function ViewProfile(props,navigation) {

  const [userData, setUserData] = React.useState({});
  const [markedDataIn, setMarkedDataIn] = React.useState({});
  const [markedDataOut, setMarkedDataOut] = React.useState({});
  const [currentIn, setCurrentIn] = React.useState("");
  const [currentOut, setCurrentOut] = React.useState("");
  const [currentDate, setCurrentDate] = React.useState([]);
  const animationRef = React.useRef();
  const refRBSheetBuy = React.useRef();


  useEffect(() => {
    animationRef.current?.play()
  }, [])

  function timeConverter(UNIX_timestamp){
    var a = new Date(UNIX_timestamp * 1000);
    var months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    var year = a.getFullYear();
    var month = months[a.getMonth()];
    var date = a.getDate();
    var hour = a.getHours();
    var min = a.getMinutes();
    var sec = a.getSeconds();
    var time =  hour + ':' + min + "  " + date + ' ' + month + ' ' + year;
    return time;
  }

  function timeDiff( tstart, tend ) {
    var diff = Math.floor(((tend*1000) - (tstart*1000)) )
    return diff / 3600000

  }


  useEffect(() => {

    var markArrayIn = []
    var inside =[]
    var dataIn =[]

    var markArrayOut = []
    var outside =[]
    var dataOut =[]


    firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).collection("people").doc(props?.route?.params?.profileUID).onSnapshot((querySnapshot) => {setUserData(querySnapshot?.data())})

    firebase.firestore().collection("att_users").doc(props?.route?.params?.uid).collection("people").doc(props?.route?.params?.profileUID).collection("attendance")
    .onSnapshot((querySnapshot) => {

        querySnapshot.forEach((documentSnapshot) => {

            console.log("EACH DATA ====>",documentSnapshot?.data() )
            dataIn.push(documentSnapshot?.data()?.in)
            dataOut.push(documentSnapshot?.data()?.out)

        })


            dataIn?.map((data)=>{

                    var a = new Date(data * 1000);
                    var months = ['01','02','03','04','05','06','07','08','09','10','11','12'];
                    var year = a.getFullYear();
                    var month = months[a.getMonth()];
                    var date = a.getDate().toString().padStart(2, '0');
                    var hour = a.getHours();
                    var min = a.getMinutes();

                    var dateData = (year + '-' + month + '-' + date).toString()
                    var timeData = year + '-' + month + '-' + date + ' ' + hour + ':' + min ;

                    markArrayIn.push(dateData)

                    console.log("DATA from data push ==> ", date)

                })
            markArrayIn?.map((data,index)=>{
                inside.push([data,{ selected: true , selectedColor: '#0CA92A', selectedTextColor: '#fff' }])
            })
            setMarkedDataIn(inside.reduce(function(prev,curr){


                        prev[curr[0]]=curr[1];
                        return prev;
                      },{}
                        ))




            dataOut?.map((data)=>{

                var a = new Date(data * 1000);
                var months = ['01','02','03','04','05','06','07','08','09','10','11','12'];
                var year = a.getFullYear();
                var month = months[a.getMonth()];
                var date = a.getDate().toString().padStart(2, '0');
                var hour = a.getHours();
                var min = a.getMinutes();

                var dateData = (year + '-' + month + '-' + date).toString()
                var timeData = year + '-' + month + '-' + date + ' ' + hour + ':' + min ;

                markArrayOut.push(dateData)

            })
            markArrayOut?.map((data,index)=>{
                outside.push([data,{ selected: true , selectedColor: '#0CA92A', selectedTextColor: '#fff' }])
            })
            setMarkedDataOut(outside.reduce(function(prev,curr){prev[curr[0]]=curr[1];return prev;},{}))



    })


}, [])



const getAttData =(date)=>{


  refRBSheetBuy.current.open()

    firebase.firestore().collection('att_users').doc(props?.route?.params?.uid).collection("people").doc(props?.route?.params?.profileUID).collection("attendance").where("date","==",date)
    .onSnapshot(querySnapshot => {


      var data = []

      querySnapshot.forEach((documentSnapshot) => {
            setCurrentIn(documentSnapshot?.data()?.in)
            setCurrentOut(documentSnapshot?.data()?.out)
            data.push(documentSnapshot?.data())


        })
        console.log("DATA ===> ",data )


        setCurrentDate(data)


        })


}

console.log(" iiii ===> ", markedDataIn)



  return ( 

<SafeAreaView style={{backgroundColor:"#EEE",height:"100%"}}>

    <View style={{flexDirection:"row",justifyContent:"space-between",alignItems:"center",marginBottom:15,marginTop:10}}>

                <TouchableOpacity style={{flexDirection:"row",justifyContent:"space-evenly",alignItems:"flex-start",marginLeft:10}}  
                onPress={()=> props?.navigation?.navigate( props?.route?.params?.comingFrom ? "ScannerScreen"  :"HomeScreen")}  >
                        <Ionicons name="md-chevron-back-outline" size={35} color="black" />
                        <Text style={{fontSize:30,fontFamily:"UberMoveMedium",color:"#000",marginLeft:-5}}>Calendar</Text>
                </TouchableOpacity>

                <TouchableOpacity style={{borderRadius:6,flexDirection:"row",justifyContent:"space-evenly",alignItems:"center",marginRight:20}}  
                onPress={()=> props?.route?.params?.comingFrom 
                        ?  Alert.alert('Logout?', 'you can login again anytime', [
                          {
                            text: 'Confirm',
                            onPress: () => {
                              firebase.auth().signOut()
                          },
                          },{
                            text: "Cancel",
                            onPress: () => console.log("Cancel Pressed"),
                            style: "cancel"
                          },
                        ])
                        : props?.navigation?.navigate("EditProfile",{profileUID : props?.route?.params?.profileUID}) }  >
                      <Feather name="settings" size={30} />
                </TouchableOpacity>


    </View>



      {markedDataIn.length !=0 
        ?<CalendarList  
           markedDates={markedDataIn}
           onDayPress={(day) => {
                    console.log("DAY ====> ",JSON.stringify(day))
                    getAttData(day?.dateString)

                }}
          />

      :<View style={{flex:1,width:"60%",marginLeft:"18%",marginTop:-50}}>
                              <LottieView
                                  ref={animationRef}
                                  autoPlay
                                  loop={true}
                                  style={{flex:1,justifyContent:"center",alignItems:"center"}}
                                  source={require("../assets/qr.json")}
                                />
                            </View>
        }


    <RBSheet
              ref={refRBSheetBuy}
              closeOnDragDown={true}
              closeOnPressMask={true}
              onClose={() => setCurrentDate([]) }
              animationType={"slide"}
              customStyles={{
              container:{
              padding:20,

              height:"auto",
              width:"100%",
              borderRadius:10,



              },
              wrapper: {
              },
              draggableIcon: {
              backgroundColor: "#fff"
              }
              }}
              >


            <View style={{marginTop:-15}}>

            <Text style={{fontSize:30,fontFamily:"UberMoveMedium",color:"#000",marginLeft:5,marginBottom:25}}>Timestamp </Text>

            { currentDate.length != 0 ?  currentDate?.map((data,key)=>{

              return <View style={{backgroundColor:"#EEE",borderColor:"#000",borderWidth:0.5,borderRadius:6,padding:10,marginBottom:12,paddingVertical:15}}>

                          <View style={{flexDirection:"row",justifyContent:"space-between",alignItems:"center",marginBottom:10}}>

                                        <Text style={{fontSize:25,fontFamily:"UberMoveMedium",color:"#000",marginLeft:5}}>In:</Text>
                                        <Text style={{fontSize:25,fontFamily:"UberMoveMedium",color:"#000",marginLeft:5}}>{timeConverter(data?.in)}</Text>

                          </View>

                          <View style={{flexDirection:"row",justifyContent:"space-between",alignItems:"center"}}>

                                        <Text style={{fontSize:25,fontFamily:"UberMoveMedium",color:"#000",marginLeft:5}}>Out:</Text>
